package exercises.akka.app;

import exercises.akka.list.LinkedList;

import static exercises.akka.utilities.StringUtils.join;
import static exercises.akka.utilities.StringUtils.split;
import static exercises.akka.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
